namespace Kdyby\Translation\Diagnostics;

use Kdyby;
use Kdyby\Translation\InvalidResourceException;
use Kdyby\Translation\Translator;
use Nette;
use Symfony\Component\Yaml;



/**
 * @author Filip Procházka <filip@prochazka.su>
 */
class Panel extends Nette\Object implements Nette\Diagnostics\IBarPanel
{

	/**
	 * @var \Kdyby\Translation\Translator
	 */
	private $translator;

	/**
	 * @var array
	 */
	private $untranslated = array();



	/**
	 * @param Translator $translator
	 */
	public function __construct(Translator $translator)
	{
		$this->translator = $translator;
	}



	/**
	 * Renders HTML code for custom tab.
	 *
	 * @return string
	 */
	public function getTab()
	{
		return '<span title="Translation"><img width="16px" height="16px" src="data:image/png;base64," />'
			. $this->translator->getLocale() . ($this->untranslated ? ' <b>(' . count(array_unique($this->untranslated)) . ' errors)</b>' : '')
			. '</span>';
	}



	/**
	 * Renders HTML code for custom panel.
	 *
	 * @return string
	 */
	public function getPanel()
	{
		$s = '';
		$h = 'htmlSpecialChars';

		foreach ($unique = array_unique($this->untranslated) as $message) {
			$s .= '<tr><td>';

			if ($message instanceof \Exception) {
				$s .= '<span style="color:red">' . $h($message->getMessage()) . '</span>';

			} elseif ($message instanceof Nette\Utils\Html) {
				$s .= '<span style="color:red">Nette\Utils\Html(' . $h((string) $message) . ')</span>';

			} else {
				$s .= $h($message);
			}

			$s .= '</td></tr>';
		}

		return empty($this->untranslated) ? '' :
			'<h1>Missing translations: ' .  count($unique) . '</h1>' .
			'<div class="nette-inner kdyby-TranslationPanel"><table><tr><th>Message</th></tr>' . $s . '</table></div>';
	}



	public function markUntranslated($id)
	{
		$this->untranslated[] = $id;
	}



	public function choiceError(\Exception $e)
	{
		$this->untranslated[] = $e;
	}



	/**
	 * @param Translator $translator
	 * @return Panel
	 */
	public static function register(Translator $translator)
	{
		$panel = new static($translator);
		/** @var Panel $panel */
		$translator->injectPanel($panel);
		Nette\Diagnostics\Debugger::$bar->addPanel($panel, 'kdyby.translation');
		return $panel;
	}



	public static function renderException(\Exception $e = NULL)
	{
		if (!$e instanceof InvalidResourceException) {
			return NULL;
		}

		$previous = $e->getPrevious();
		if (!$previous) {
			return NULL;
		}

		$previous = $previous->getPrevious();
		if (!$previous instanceof Yaml\Exception\ParseException) {
			return NULL;
		}

		if ($call = Nette\Diagnostics\Helpers::findTrace($e->getPrevious()->getTrace(), 'Symfony\Component\Translation\Loader\YamlFileLoader::load')) {
			return array(
				'tab' => 'YAML dictionary',
				'panel' => '<p>' . $previous->getMessage() . '</p><p><b>File:</b> ' . Nette\Diagnostics\Helpers::editorLink($call['args'][0], $previous->getParsedLine()) . '</p>'
				. ($previous->getParsedLine() ? Nette\Diagnostics\BlueScreen::highlightFile($call['args'][0], $previous->getParsedLine()) : '')
			);
		}
	}

}
